import React from "react";
import { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import backgroundImage from "../assets/home.jpg";
import MovieLogo from "../assets/homeTitle.webp";
import { FaPlay } from "react-icons/fa";
import { AiOutlineInfoCircle } from "react-icons/ai";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import Slider from "../components/Slider";
import { fetchMovies, getGenres } from "../store";
import { RiTicket2Line } from "react-icons/ri";

export default function Tmovies() {
  const [isScrolled, setIsScrolled] = useState(false);
  const navigate = useNavigate();
  const genresLoaded = useSelector((state) => state.tmovies.genresLoaded);
  const movies = useSelector((state) => state.tmovies.movies) 
  const dispatch = useDispatch();

  // const [modal, setModal] = useState(false);
  // const toggleModal = () => {
  //   setModal(!modal)
  // }

  useEffect(() => {
    dispatch(getGenres());
  }, []);

  useEffect(()=> {
    if(genresLoaded) dispatch(fetchMovies({ type: "all" }));
  }, [genresLoaded]);

  window.onscroll = () => {
    setIsScrolled(window.pageYOffset === 0 ? false : true);
    return () => (window.onscroll = null);
  };

  // console.log(movies);

  return (
    <Container>
      <Navbar isScrolled={isScrolled} />
      <div className="hero">
        <img
          src={backgroundImage}
          alt="background"
          className="background-image"
        />
        <div className="container">
          <div className="logo">
            <img src={MovieLogo} alt="Movie Logo" />
          </div>
          <div className="buttons flex">
            <button
              className="flex j-center a-center"
              href={"http://localhost:3013/"}
              target="_blank"
              onClick={() => navigate("/player")}
            >
              <FaPlay />
              Watch Trailer
            </button>
            <button className="flex j-center a-center" onClick={()=>navigate("/detail")}>
              <AiOutlineInfoCircle />
              More Detail
            </button>

            <button className="flex j-center a-center" onClick={()=>navigate("/ticket")}>
              <RiTicket2Line />
              Get Ticket
            </button>

            <button1 onClick={toggleModal} className="btn-modal">
              Get Ticket
            </button1>

            <div className="modal">
              <div className="overplay"></div>
              <div className="modal-content">
                <h2>Ticket The Movies</h2>
                <p>
                  Lorem ipsum dolor sit amet consectetur, adipisicing elit. 
                  Expedita, nobis, blanditiis nemo quia ab vel quibusdam cupiditate 
                  corporis voluptas laudantium consectetur repellat nisi accusantium 
                  porro amet quae modi officia nihil!
                </p>
              </div>
            </div>

          </div>
        </div>
      </div>
      <Slider movies={movies}/>
    </Container>
  );
}

const Container = styled.div`
  background-color: black;
  .hero {
    position: relative;
    .background-image {
      filter: brightness(60%);
    }
    img {
      height: 100vh;
      width: 100wv;
    }
    .container {
      position: absolute;
      bottom: 5rem;
      .logo {
        img {
          width: 100%;
          height: 100%;
          margin-left: 5rem;
        }
      }
      .buttons {
        margin: 5rem;
        gap: 2rem;
        button {
          font-size: 1.4rem;
          gap: 1rem;
          border-radius: 0.2rem;
          padding: 0.5rem;
          padding-left: 2rem;
          padding-right: 2.4rem;
          border: none;
          cursor: pointer;
          transition: 0.5s ease-in-out;
          &:hover {
            opacity: 0.8;
          }
          &:nth-of-type(2) {
            background-color: rgba(109, 109, 110, 0.7);
            color: white;
            svg {
              font-size: 1.8rem;
            }
          }
        }
      }
    }
  }
`;
