import React, {useEffect, useState} from "react"
import { useParams } from "react-router-dom"
import Button from 'react-bootstrap/Button';

const Movie = () => {
    const [currentMovieDetail, setMovie] = useState()
    const { id } = useParams()

    useEffect(() => {
        getData()
        window.scrollTo(0,0)
    }, [])

    const getData = () => {
        fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=4e44d9029b1270a757cddc766a1bcb63&language=en-US`)
        .then(res => res.json())
        .then(data => setMovie(data))
    }

    return (
        <div className="movie">
            <div className="movie__intro">
                <img className="movie__backdrop" src={`https://image.tmdb.org/t/p/original${currentMovieDetail ? currentMovieDetail.backdrop_path : ""}`} />
            </div>
            <div className="movie__detail">
                <div className="movie__detailLeft">
                    <div className="movie__posterBox">
                        <img className="movie__poster" src={`https://image.tmdb.org/t/p/original${currentMovieDetail ? currentMovieDetail.poster_path : ""}`} />
                    </div>
                </div>
                <Button variant="light">12.03</Button>{' '}
                <Button variant="light">13.10</Button>{' '}
                <Button variant="light">14.25</Button>{' '}
                <Button variant="light">15.30</Button>{' '}
                <Button variant="light">17.00</Button>{' '}
                <Button variant="light">19.10</Button>{' '}
                <Button variant="light">20.20</Button>{' '}

            </div>

        </div>
    )
}

export default Movie




