import axios from "axios";
import React, { useEffect, useState } from "react";
import CardSlider from "./CardSlider";

export default function Slider({ movies }) {
  const getMoviesFromRange = (from, to) => {
    return movies.slice(from, to);
  };

  // console.log(movies);

  const [film, setFilm] = useState([]);
  const [isLoadingFilm, setIsLoadingFilm] = useState(false);

  const getFilm = async () => {
    setIsLoadingFilm(true);

    await axios.get("http://192.168.1.4:8000/api/Movies").then((res) => {
      const mappedData = res.data.map((val) => {
        return {
          id: val.id,
          image: `${`http://192.168.1.4:8000`}/${val.img}`,
          name: val.name,
          video: `${`http://192.168.1.4:8000`}/${val.vid}`,
          genres: [],
        };
      });
      setFilm(mappedData);
    });

    await axios
      .get("http://192.168.1.4:8000/api/Movies/toprated")
      .then((res) => {
        const mappedData = res.data.map((val) => {
          return {
            id: val.id,
            image: `${`http://192.168.1.4:8000`}/${val.img}`,
            name: val.name,
            video: `${`http://192.168.1.4:8000`}/${val.vid}`,
            genres: [],
          };
        });
        setFilm(mappedData);
      });

    setIsLoadingFilm(false);
  };

  //   console.log("ini film", film);

  useEffect(() => {
    getFilm();
  }, []);

  return (
    <div>
      {isLoadingFilm ? (
        <h1>Loading...</h1>
      ) : (
        <CardSlider title="Trending Now" data={film} />
      )}
      <CardSlider title="New Releases" data={film} />
      <CardSlider
        title="Blockbuster Movies"
        data={getMoviesFromRange(20, 30)}
      />
      <CardSlider title="Popular" data={getMoviesFromRange(30, 40)} />
      {/* <CardSlider title="Action Movies" data={getMoviesFromRange(40, 50)} /> */}
    </div>
  );
}
